import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";import{o as d,c as l,a as p,b as t,e as o,d as a,r}from"./app.26b963d6.js";const u={},h=o('<h2 id="\u4E00-\u673A\u5668\u6570\u548C\u771F\u503C" tabindex="-1"><a class="header-anchor" href="#\u4E00-\u673A\u5668\u6570\u548C\u771F\u503C" aria-hidden="true">#</a> \u4E00 \u673A\u5668\u6570\u548C\u771F\u503C</h2><p>\u5728\u5B66\u4E60\u539F\u7801, \u53CD\u7801\u548C\u8865\u7801\u4E4B\u524D, \u9700\u8981\u5148\u4E86\u89E3\u673A\u5668\u6570\u548C\u771F\u503C\u7684\u6982\u5FF5.</p><h3 id="_1\u3001\u673A\u5668\u6570" tabindex="-1"><a class="header-anchor" href="#_1\u3001\u673A\u5668\u6570" aria-hidden="true">#</a> 1\u3001\u673A\u5668\u6570</h3><p>\u4E00\u4E2A\u6570\u5728\u8BA1\u7B97\u673A\u4E2D\u7684\u4E8C\u8FDB\u5236\u8868\u793A\u5F62\u5F0F, \u53EB\u505A\u8FD9\u4E2A\u6570\u7684\u673A\u5668\u6570\u3002\u673A\u5668\u6570\u662F\u5E26\u7B26\u53F7\u7684\uFF0C\u5728\u8BA1\u7B97\u673A\u7528\u4E00\u4E2A\u6570\u7684\u6700\u9AD8\u4F4D\u5B58\u653E\u7B26\u53F7, \u6B63\u6570\u4E3A0, \u8D1F\u6570\u4E3A1.</p><p>\u6BD4\u5982\uFF0C\u5341\u8FDB\u5236\u4E2D\u7684\u6570 +3 \uFF0C\u8BA1\u7B97\u673A\u5B57\u957F\u4E3A8\u4F4D\uFF0C\u8F6C\u6362\u6210\u4E8C\u8FDB\u5236\u5C31\u662F00000011\u3002\u5982\u679C\u662F -3 \uFF0C\u5C31\u662F 10000011 \u3002</p><p>\u90A3\u4E48\uFF0C\u8FD9\u91CC\u7684 00000011 \u548C 10000011 \u5C31\u662F\u673A\u5668\u6570\u3002</p><h3 id="_2\u3001\u771F\u503C" tabindex="-1"><a class="header-anchor" href="#_2\u3001\u771F\u503C" aria-hidden="true">#</a> 2\u3001\u771F\u503C</h3><blockquote><p>\u56E0\u4E3A\u7B2C\u4E00\u4F4D\u662F\u7B26\u53F7\u4F4D\uFF0C\u6240\u4EE5\u673A\u5668\u6570\u7684\u5F62\u5F0F\u503C\u5C31\u4E0D\u7B49\u4E8E\u771F\u6B63\u7684\u6570\u503C\u3002\u4F8B\u5982\u4E0A\u9762\u7684\u6709\u7B26\u53F7\u6570 10000011\uFF0C\u5176\u6700\u9AD8\u4F4D1\u4EE3\u8868\u8D1F\uFF0C\u5176\u771F\u6B63\u6570\u503C\u662F -3 \u800C\u4E0D\u662F\u5F62\u5F0F\u503C131\uFF0810000011\u8F6C\u6362\u6210\u5341\u8FDB\u5236\u7B49\u4E8E131\uFF09\u3002\u6240\u4EE5\uFF0C\u4E3A\u533A\u522B\u8D77\u89C1\uFF0C\u5C06\u5E26\u7B26\u53F7\u4F4D\u7684\u673A\u5668\u6570\u5BF9\u5E94\u7684\u771F\u6B63\u6570\u503C\u79F0\u4E3A\u673A\u5668\u6570\u7684\u771F\u503C\u3002</p></blockquote><p>\u4F8B\uFF1A0000 0001\u7684\u771F\u503C = +000 0001 = +1\uFF0C1000 0001\u7684\u771F\u503C = \u2013000 0001 = \u20131</p><h2 id="\u4E8C-\u539F\u7801-\u53CD\u7801-\u8865\u7801\u7684\u57FA\u7840\u6982\u5FF5\u548C\u8BA1\u7B97\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u4E8C-\u539F\u7801-\u53CD\u7801-\u8865\u7801\u7684\u57FA\u7840\u6982\u5FF5\u548C\u8BA1\u7B97\u65B9\u6CD5" aria-hidden="true">#</a> \u4E8C \u539F\u7801, \u53CD\u7801, \u8865\u7801\u7684\u57FA\u7840\u6982\u5FF5\u548C\u8BA1\u7B97\u65B9\u6CD5.</h2><p>\u5728\u63A2\u6C42\u4E3A\u4F55\u673A\u5668\u8981\u4F7F\u7528\u8865\u7801\u4E4B\u524D, \u8BA9\u6211\u4EEC\u5148\u4E86\u89E3\u539F\u7801, \u53CD\u7801\u548C\u8865\u7801\u7684\u6982\u5FF5.\u5BF9\u4E8E\u4E00\u4E2A\u6570, \u8BA1\u7B97\u673A\u8981\u4F7F\u7528\u4E00\u5B9A\u7684\u7F16\u7801\u65B9\u5F0F\u8FDB\u884C\u5B58\u50A8. \u539F\u7801, \u53CD\u7801, \u8865\u7801\u662F\u673A\u5668\u5B58\u50A8\u4E00\u4E2A\u5177\u4F53\u6570\u5B57\u7684\u7F16\u7801\u65B9\u5F0F.</p><h3 id="_1-\u539F\u7801" tabindex="-1"><a class="header-anchor" href="#_1-\u539F\u7801" aria-hidden="true">#</a> 1. \u539F\u7801</h3><p>\u539F\u7801\u5C31\u662F\u7B26\u53F7\u4F4D\u52A0\u4E0A\u771F\u503C\u7684\u7EDD\u5BF9\u503C, \u5373\u7528\u7B2C\u4E00\u4F4D\u8868\u793A\u7B26\u53F7, \u5176\u4F59\u4F4D\u8868\u793A\u503C. \u6BD4\u5982\u5982\u679C\u662F8\u4F4D\u4E8C\u8FDB\u5236:</p><blockquote><p>[+1]\u539F = 0000 0001</p><p>[-1]\u539F = 1000 0001</p></blockquote><p>\u7B2C\u4E00\u4F4D\u662F\u7B26\u53F7\u4F4D. \u56E0\u4E3A\u7B2C\u4E00\u4F4D\u662F\u7B26\u53F7\u4F4D, \u6240\u4EE58\u4F4D\u4E8C\u8FDB\u5236\u6570\u7684\u53D6\u503C\u8303\u56F4\u5C31\u662F:</p><blockquote><p>[1111 1111 , 0111 1111]</p></blockquote><p>\u5373</p><blockquote><p>[-127 , 127]</p></blockquote><p>\u539F\u7801\u662F\u4EBA\u8111\u6700\u5BB9\u6613\u7406\u89E3\u548C\u8BA1\u7B97\u7684\u8868\u793A\u65B9\u5F0F.</p><h3 id="_2-\u53CD\u7801" tabindex="-1"><a class="header-anchor" href="#_2-\u53CD\u7801" aria-hidden="true">#</a> 2. \u53CD\u7801</h3><p>\u53CD\u7801\u7684\u8868\u793A\u65B9\u6CD5\u662F:</p><p>\u6B63\u6570\u7684\u53CD\u7801\u662F\u5176\u672C\u8EAB</p><p>\u8D1F\u6570\u7684\u53CD\u7801\u662F\u5728\u5176\u539F\u7801\u7684\u57FA\u7840\u4E0A, \u7B26\u53F7\u4F4D\u4E0D\u53D8\uFF0C\u5176\u4F59\u5404\u4E2A\u4F4D\u53D6\u53CD.</p><blockquote><p>[+1] = [00000001]\u539F = [00000001]\u53CD</p><p>[-1] = [10000001]\u539F = [11111110]\u53CD</p></blockquote><p>\u53EF\u89C1\u5982\u679C\u4E00\u4E2A\u53CD\u7801\u8868\u793A\u7684\u662F\u8D1F\u6570, \u4EBA\u8111\u65E0\u6CD5\u76F4\u89C2\u7684\u770B\u51FA\u6765\u5B83\u7684\u6570\u503C. \u901A\u5E38\u8981\u5C06\u5176\u8F6C\u6362\u6210\u539F\u7801\u518D\u8BA1\u7B97.</p><h3 id="_3-\u8865\u7801" tabindex="-1"><a class="header-anchor" href="#_3-\u8865\u7801" aria-hidden="true">#</a> 3. \u8865\u7801</h3><p>\u8865\u7801\u7684\u8868\u793A\u65B9\u6CD5\u662F:</p><p>\u6B63\u6570\u7684\u8865\u7801\u5C31\u662F\u5176\u672C\u8EAB</p><p>\u8D1F\u6570\u7684\u8865\u7801\u662F\u5728\u5176\u539F\u7801\u7684\u57FA\u7840\u4E0A, \u7B26\u53F7\u4F4D\u4E0D\u53D8, \u5176\u4F59\u5404\u4F4D\u53D6\u53CD, \u6700\u540E+1. (\u5373\u5728\u53CD\u7801\u7684\u57FA\u7840\u4E0A+1)</p><blockquote><p>[+1] = [00000001]\u539F = [00000001]\u53CD = [00000001]\u8865</p><p>[-1] = [10000001]\u539F = [11111110]\u53CD = [11111111]\u8865</p></blockquote><p>\u5BF9\u4E8E\u8D1F\u6570, \u8865\u7801\u8868\u793A\u65B9\u5F0F\u4E5F\u662F\u4EBA\u8111\u65E0\u6CD5\u76F4\u89C2\u770B\u51FA\u5176\u6570\u503C\u7684. \u901A\u5E38\u4E5F\u9700\u8981\u8F6C\u6362\u6210\u539F\u7801\u5728\u8BA1\u7B97\u5176\u6570\u503C.</p><h2 id="\u4E09-\u4E3A\u4F55\u8981\u4F7F\u7528\u539F\u7801-\u53CD\u7801\u548C\u8865\u7801" tabindex="-1"><a class="header-anchor" href="#\u4E09-\u4E3A\u4F55\u8981\u4F7F\u7528\u539F\u7801-\u53CD\u7801\u548C\u8865\u7801" aria-hidden="true">#</a> \u4E09. \u4E3A\u4F55\u8981\u4F7F\u7528\u539F\u7801, \u53CD\u7801\u548C\u8865\u7801</h2><p>\u5728\u5F00\u59CB\u6DF1\u5165\u5B66\u4E60\u524D, \u6211\u7684\u5B66\u4E60\u5EFA\u8BAE\u662F\u5148&quot;\u6B7B\u8BB0\u786C\u80CC&quot;\u4E0A\u9762\u7684\u539F\u7801, \u53CD\u7801\u548C\u8865\u7801\u7684\u8868\u793A\u65B9\u5F0F\u4EE5\u53CA\u8BA1\u7B97\u65B9\u6CD5.</p><p>\u73B0\u5728\u6211\u4EEC\u77E5\u9053\u4E86\u8BA1\u7B97\u673A\u53EF\u4EE5\u6709\u4E09\u79CD\u7F16\u7801\u65B9\u5F0F\u8868\u793A\u4E00\u4E2A\u6570. \u5BF9\u4E8E\u6B63\u6570\u56E0\u4E3A\u4E09\u79CD\u7F16\u7801\u65B9\u5F0F\u7684\u7ED3\u679C\u90FD\u76F8\u540C:</p><blockquote><p>[+1] = [00000001]\u539F = [00000001]\u53CD = [00000001]\u8865</p></blockquote><p>\u6240\u4EE5\u4E0D\u9700\u8981\u8FC7\u591A\u89E3\u91CA. \u4F46\u662F\u5BF9\u4E8E\u8D1F\u6570:</p><blockquote><p>[-1] = [10000001]\u539F = [11111110]\u53CD = [11111111]\u8865</p></blockquote><p>\u53EF\u89C1\u539F\u7801, \u53CD\u7801\u548C\u8865\u7801\u662F\u5B8C\u5168\u4E0D\u540C\u7684. \u65E2\u7136\u539F\u7801\u624D\u662F\u88AB\u4EBA\u8111\u76F4\u63A5\u8BC6\u522B\u5E76\u7528\u4E8E\u8BA1\u7B97\u8868\u793A\u65B9\u5F0F, \u4E3A\u4F55\u8FD8\u4F1A\u6709\u53CD\u7801\u548C\u8865\u7801\u5462?</p><p>\u9996\u5148, \u56E0\u4E3A\u4EBA\u8111\u53EF\u4EE5\u77E5\u9053\u7B2C\u4E00\u4F4D\u662F\u7B26\u53F7\u4F4D, \u5728\u8BA1\u7B97\u7684\u65F6\u5019\u6211\u4EEC\u4F1A\u6839\u636E\u7B26\u53F7\u4F4D, \u9009\u62E9\u5BF9\u771F\u503C\u533A\u57DF\u7684\u52A0\u51CF. (\u771F\u503C\u7684\u6982\u5FF5\u5728\u672C\u6587\u6700\u5F00\u5934). \u4F46\u662F\u5BF9\u4E8E\u8BA1\u7B97\u673A, \u52A0\u51CF\u4E58\u6570\u5DF2\u7ECF\u662F\u6700\u57FA\u7840\u7684\u8FD0\u7B97, \u8981\u8BBE\u8BA1\u7684\u5C3D\u91CF\u7B80\u5355. \u8BA1\u7B97\u673A\u8FA8\u522B&quot;\u7B26\u53F7\u4F4D&quot;\u663E\u7136\u4F1A\u8BA9\u8BA1\u7B97\u673A\u7684\u57FA\u7840\u7535\u8DEF\u8BBE\u8BA1\u53D8\u5F97\u5341\u5206\u590D\u6742! \u4E8E\u662F\u4EBA\u4EEC\u60F3\u51FA\u4E86\u5C06\u7B26\u53F7\u4F4D\u4E5F\u53C2\u4E0E\u8FD0\u7B97\u7684\u65B9\u6CD5. \u6211\u4EEC\u77E5\u9053, \u6839\u636E\u8FD0\u7B97\u6CD5\u5219\u51CF\u53BB\u4E00\u4E2A\u6B63\u6570\u7B49\u4E8E\u52A0\u4E0A\u4E00\u4E2A\u8D1F\u6570, \u5373: 1-1 = 1 + (-1) = 0 , \u6240\u4EE5\u673A\u5668\u53EF\u4EE5\u53EA\u6709\u52A0\u6CD5\u800C\u6CA1\u6709\u51CF\u6CD5, \u8FD9\u6837\u8BA1\u7B97\u673A\u8FD0\u7B97\u7684\u8BBE\u8BA1\u5C31\u66F4\u7B80\u5355\u4E86.</p><p>\u4E8E\u662F\u4EBA\u4EEC\u5F00\u59CB\u63A2\u7D22 \u5C06\u7B26\u53F7\u4F4D\u53C2\u4E0E\u8FD0\u7B97, \u5E76\u4E14\u53EA\u4FDD\u7559\u52A0\u6CD5\u7684\u65B9\u6CD5. \u9996\u5148\u6765\u770B\u539F\u7801:</p><p>\u8BA1\u7B97\u5341\u8FDB\u5236\u7684\u8868\u8FBE\u5F0F: 1-1=0</p><blockquote><p>1 - 1 = 1 + (-1) = [00000001]\u539F + [10000001]\u539F = [10000010]\u539F = -2</p></blockquote><p>\u5982\u679C\u7528\u539F\u7801\u8868\u793A, \u8BA9\u7B26\u53F7\u4F4D\u4E5F\u53C2\u4E0E\u8BA1\u7B97, \u663E\u7136\u5BF9\u4E8E\u51CF\u6CD5\u6765\u8BF4, \u7ED3\u679C\u662F\u4E0D\u6B63\u786E\u7684.\u8FD9\u4E5F\u5C31\u662F\u4E3A\u4F55\u8BA1\u7B97\u673A\u5185\u90E8\u4E0D\u4F7F\u7528\u539F\u7801\u8868\u793A\u4E00\u4E2A\u6570.</p><p>\u4E3A\u4E86\u89E3\u51B3\u539F\u7801\u505A\u51CF\u6CD5\u7684\u95EE\u9898, \u51FA\u73B0\u4E86\u53CD\u7801:</p><p>\u8BA1\u7B97\u5341\u8FDB\u5236\u7684\u8868\u8FBE\u5F0F: 1-1=0</p><blockquote><p>1 - 1 = 1 + (-1) = [0000 0001]\u539F + [1000 0001]\u539F= [0000 0001]\u53CD + [1111 1110]\u53CD = [1111 1111]\u53CD = [1000 0000]\u539F = -0</p></blockquote><p>\u53D1\u73B0\u7528\u53CD\u7801\u8BA1\u7B97\u51CF\u6CD5, \u7ED3\u679C\u7684\u771F\u503C\u90E8\u5206\u662F\u6B63\u786E\u7684. \u800C\u552F\u4E00\u7684\u95EE\u9898\u5176\u5B9E\u5C31\u51FA\u73B0\u5728&quot;0&quot;\u8FD9\u4E2A\u7279\u6B8A\u7684\u6570\u503C\u4E0A. \u867D\u7136\u4EBA\u4EEC\u7406\u89E3\u4E0A+0\u548C-0\u662F\u4E00\u6837\u7684, \u4F46\u662F0\u5E26\u7B26\u53F7\u662F\u6CA1\u6709\u4EFB\u4F55\u610F\u4E49\u7684. \u800C\u4E14\u4F1A\u6709[0000 0000]\u539F\u548C[1000 0000]\u539F\u4E24\u4E2A\u7F16\u7801\u8868\u793A0.</p><p>\u4E8E\u662F\u8865\u7801\u7684\u51FA\u73B0, \u89E3\u51B3\u4E860\u7684\u7B26\u53F7\u4EE5\u53CA\u4E24\u4E2A\u7F16\u7801\u7684\u95EE\u9898:</p><blockquote><p>1-1 = 1 + (-1) = [0000 0001]\u539F + [1000 0001]\u539F = [0000 0001]\u8865 + [1111 1111]\u8865 = [0000 0000]\u8865=[0000 0000]\u539F</p></blockquote><p>\u8FD9\u68370\u7528[0000 0000]\u8868\u793A, \u800C\u4EE5\u524D\u51FA\u73B0\u95EE\u9898\u7684-0\u5219\u4E0D\u5B58\u5728\u4E86.\u800C\u4E14\u53EF\u4EE5\u7528[1000 0000]\u8868\u793A-128:</p><blockquote><p>(-1) + (-127) = [1000 0001]\u539F + [1111 1111]\u539F = [1111 1111]\u8865 + [1000 0001]\u8865 = [1000 0000]\u8865</p></blockquote><p>-1-127\u7684\u7ED3\u679C\u5E94\u8BE5\u662F-128, \u5728\u7528\u8865\u7801\u8FD0\u7B97\u7684\u7ED3\u679C\u4E2D, [1000 0000]\u8865 \u5C31\u662F-128. \u4F46\u662F\u6CE8\u610F\u56E0\u4E3A\u5B9E\u9645\u4E0A\u662F\u4F7F\u7528\u4EE5\u524D\u7684-0\u7684\u8865\u7801\u6765\u8868\u793A-128, \u6240\u4EE5-128\u5E76\u6CA1\u6709\u539F\u7801\u548C\u53CD\u7801\u8868\u793A.(\u5BF9-128\u7684\u8865\u7801\u8868\u793A[1000 0000]\u8865\u7B97\u51FA\u6765\u7684\u539F\u7801\u662F[0000 0000]\u539F, \u8FD9\u662F\u4E0D\u6B63\u786E\u7684)</p><p>\u4F7F\u7528\u8865\u7801, \u4E0D\u4EC5\u4EC5\u4FEE\u590D\u4E860\u7684\u7B26\u53F7\u4EE5\u53CA\u5B58\u5728\u4E24\u4E2A\u7F16\u7801\u7684\u95EE\u9898, \u800C\u4E14\u8FD8\u80FD\u591F\u591A\u8868\u793A\u4E00\u4E2A\u6700\u4F4E\u6570. \u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E488\u4F4D\u4E8C\u8FDB\u5236, \u4F7F\u7528\u539F\u7801\u6216\u53CD\u7801\u8868\u793A\u7684\u8303\u56F4\u4E3A[-127, +127], \u800C\u4F7F\u7528\u8865\u7801\u8868\u793A\u7684\u8303\u56F4\u4E3A[-128, 127].</p><p>\u56E0\u4E3A\u673A\u5668\u4F7F\u7528\u8865\u7801, \u6240\u4EE5\u5BF9\u4E8E\u7F16\u7A0B\u4E2D\u5E38\u7528\u5230\u768432\u4F4Dint\u7C7B\u578B, \u53EF\u4EE5\u8868\u793A\u8303\u56F4\u662F: [-231, 231-1] \u56E0\u4E3A\u7B2C\u4E00\u4F4D\u8868\u793A\u7684\u662F\u7B26\u53F7\u4F4D.\u800C\u4F7F\u7528\u8865\u7801\u8868\u793A\u65F6\u53C8\u53EF\u4EE5\u591A\u4FDD\u5B58\u4E00\u4E2A\u6700\u5C0F\u503C.</p><h2 id="\u56DB-\u539F\u7801-\u53CD\u7801-\u8865\u7801-\u518D\u6DF1\u5165" tabindex="-1"><a class="header-anchor" href="#\u56DB-\u539F\u7801-\u53CD\u7801-\u8865\u7801-\u518D\u6DF1\u5165" aria-hidden="true">#</a> \u56DB \u539F\u7801, \u53CD\u7801, \u8865\u7801 \u518D\u6DF1\u5165</h2><p>\u8BA1\u7B97\u673A\u5DE7\u5999\u5730\u628A\u7B26\u53F7\u4F4D\u53C2\u4E0E\u8FD0\u7B97, \u5E76\u4E14\u5C06\u51CF\u6CD5\u53D8\u6210\u4E86\u52A0\u6CD5, \u80CC\u540E\u8574\u542B\u4E86\u600E\u6837\u7684\u6570\u5B66\u539F\u7406\u5462?</p><p>\u5C06\u949F\u8868\u60F3\u8C61\u6210\u662F\u4E00\u4E2A1\u4F4D\u768412\u8FDB\u5236\u6570. \u5982\u679C\u5F53\u524D\u65F6\u95F4\u662F6\u70B9, \u6211\u5E0C\u671B\u5C06\u65F6\u95F4\u8BBE\u7F6E\u62104\u70B9, \u9700\u8981\u600E\u4E48\u505A\u5462?\u6211\u4EEC\u53EF\u4EE5:</p><blockquote><p>\\1. \u5F80\u56DE\u62E82\u4E2A\u5C0F\u65F6: 6 - 2 = 4</p><p>\\2. \u5F80\u524D\u62E810\u4E2A\u5C0F\u65F6: (6 + 10) mod 12 = 4</p><p>\\3. \u5F80\u524D\u62E810+12=22\u4E2A\u5C0F\u65F6: (6+22) mod 12 =4</p></blockquote><p>2,3\u65B9\u6CD5\u4E2D\u7684mod\u662F\u6307\u53D6\u6A21\u64CD\u4F5C, 16 mod 12 =4 \u5373\u752816\u9664\u4EE512\u540E\u7684\u4F59\u6570\u662F4.</p><p>\u6240\u4EE5\u949F\u8868\u5F80\u56DE\u62E8(\u51CF\u6CD5)\u7684\u7ED3\u679C\u53EF\u4EE5\u7528\u5F80\u524D\u62E8(\u52A0\u6CD5)\u66FF\u4EE3!</p><p>\u73B0\u5728\u7684\u7126\u70B9\u5C31\u843D\u5728\u4E86\u5982\u4F55\u7528\u4E00\u4E2A\u6B63\u6570, \u6765\u66FF\u4EE3\u4E00\u4E2A\u8D1F\u6570. \u4E0A\u9762\u7684\u4F8B\u5B50\u6211\u4EEC\u80FD\u611F\u89C9\u51FA\u6765\u4E00\u4E9B\u7AEF\u502A, \u53D1\u73B0\u4E00\u4E9B\u89C4\u5F8B. \u4F46\u662F\u6570\u5B66\u662F\u4E25\u8C28\u7684. \u4E0D\u80FD\u9760\u611F\u89C9.</p><p>\u9996\u5148\u4ECB\u7ECD\u4E00\u4E2A\u6570\u5B66\u4E2D\u76F8\u5173\u7684\u6982\u5FF5: \u540C\u4F59</p><h3 id="\u540C\u4F59\u7684\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u540C\u4F59\u7684\u6982\u5FF5" aria-hidden="true">#</a> \u540C\u4F59\u7684\u6982\u5FF5</h3><p>\u4E24\u4E2A\u6574\u6570a\uFF0Cb\uFF0C\u82E5\u5B83\u4EEC\u9664\u4EE5\u6574\u6570m\u6240\u5F97\u7684\u4F59\u6570\u76F8\u7B49\uFF0C\u5219\u79F0a\uFF0Cb\u5BF9\u4E8E\u6A21m\u540C\u4F59</p><p>\u8BB0\u4F5C a \u2261 b (mod m)</p><p>\u8BFB\u4F5C a \u4E0E b \u5173\u4E8E\u6A21 m \u540C\u4F59\u3002</p><p>\u4E3E\u4F8B\u8BF4\u660E:</p><blockquote><p>4 mod 12 = 4</p><p>16 mod 12 = 4</p><p>28 mod 12 = 4</p></blockquote><p>\u6240\u4EE54, 16, 28\u5173\u4E8E\u6A21 12 \u540C\u4F59.</p><h3 id="\u8D1F\u6570\u53D6\u6A21" tabindex="-1"><a class="header-anchor" href="#\u8D1F\u6570\u53D6\u6A21" aria-hidden="true">#</a> \u8D1F\u6570\u53D6\u6A21</h3><p>\u6B63\u6570\u8FDB\u884Cmod\u8FD0\u7B97\u662F\u5F88\u7B80\u5355\u7684. \u4F46\u662F\u8D1F\u6570\u5462?</p><p>\u4E0B\u9762\u662F\u5173\u4E8Emod\u8FD0\u7B97\u7684\u6570\u5B66\u5B9A\u4E49:</p>',72),b={href:"http://images.cnblogs.com/cnblogs_com/zhangziqiu/201103/201103302155507894.jpg",target:"_blank",rel:"noopener noreferrer"},n=p("img",{src:"https://images.cnblogs.com/cnblogs_com/zhangziqiu/201103/201103302155504514.jpg",alt:"clip_image001",loading:"lazy"},null,-1),i=o('<p>\u4E0A\u9762\u662F\u622A\u56FE, &quot;\u53D6\u4E0B\u754C&quot;\u7B26\u53F7\u627E\u4E0D\u5230\u5982\u4F55\u8F93\u5165(word\u4E2D\u7C98\u8D34\u8FC7\u6765\u540E\u4E71\u7801). \u4E0B\u9762\u662F\u4F7F\u7528&quot;L&quot;\u548C&quot;J&quot;\u66FF\u6362\u4E0A\u56FE\u7684&quot;\u53D6\u4E0B\u754C&quot;\u7B26\u53F7:</p><blockquote><p>x mod y = x - y L x / y J</p></blockquote><p>\u4E0A\u9762\u516C\u5F0F\u7684\u610F\u601D\u662F:</p><p>x mod y\u7B49\u4E8E x \u51CF\u53BB y \u4E58\u4E0A x\u4E0Ey\u7684\u5546\u7684\u4E0B\u754C.</p><p>\u4EE5 -3 mod 2 \u4E3E\u4F8B:</p><blockquote><p>-3 mod 2</p><p>= -3 - 2xL -3/2 J</p><p>= -3 - 2xL-1.5J</p><p>= -3 - 2x(-2)</p><p>= -3 + 4 = 1</p></blockquote><p>\u6240\u4EE5:</p><blockquote><p>(-2) mod 12 = 12-2=10</p><p>(-4) mod 12 = 12-4 = 8</p><p>(-5) mod 12 = 12 - 5 = 7</p></blockquote><h3 id="\u5F00\u59CB\u8BC1\u660E" tabindex="-1"><a class="header-anchor" href="#\u5F00\u59CB\u8BC1\u660E" aria-hidden="true">#</a> \u5F00\u59CB\u8BC1\u660E</h3><p>\u518D\u56DE\u5230\u65F6\u949F\u7684\u95EE\u9898\u4E0A:</p><blockquote><p>\u56DE\u62E82\u5C0F\u65F6 = \u524D\u62E810\u5C0F\u65F6</p><p>\u56DE\u62E84\u5C0F\u65F6 = \u524D\u62E88\u5C0F\u65F6</p><p>\u56DE\u62E85\u5C0F\u65F6= \u524D\u62E87\u5C0F\u65F6</p></blockquote><p>\u6CE8\u610F, \u8FD9\u91CC\u53D1\u73B0\u7684\u89C4\u5F8B!</p><p>\u7ED3\u5408\u4E0A\u9762\u5B66\u5230\u7684\u540C\u4F59\u7684\u6982\u5FF5.\u5B9E\u9645\u4E0A:</p><blockquote><p>(-2) mod 12 = 10</p><p>10 mod 12 = 10</p></blockquote><p>-2\u4E0E10\u662F\u540C\u4F59\u7684.</p><blockquote><p>(-4) mod 12 = 8</p><p>8 mod 12 = 8</p></blockquote><p>-4\u4E0E8\u662F\u540C\u4F59\u7684.</p><p>\u8DDD\u79BB\u6210\u529F\u8D8A\u6765\u8D8A\u8FD1\u4E86. \u8981\u5B9E\u73B0\u7528\u6B63\u6570\u66FF\u4EE3\u8D1F\u6570, \u53EA\u9700\u8981\u8FD0\u7528\u540C\u4F59\u6570\u7684\u4E24\u4E2A\u5B9A\u7406:</p><p>\u53CD\u8EAB\u6027:</p><blockquote><p>a \u2261 a (mod m)</p></blockquote><p>\u8FD9\u4E2A\u5B9A\u7406\u662F\u5F88\u663E\u800C\u6613\u89C1\u7684.</p><p>\u7EBF\u6027\u8FD0\u7B97\u5B9A\u7406:</p><blockquote><p>\u5982\u679Ca \u2261 b (mod m)\uFF0Cc \u2261 d (mod m) \u90A3\u4E48:</p><p>(1)a \xB1 c \u2261 b \xB1 d (mod m)</p><p>(2)a * c \u2261 b * d (mod m)</p></blockquote>',23),q=a("\u5982\u679C\u60F3\u770B\u8FD9\u4E2A\u5B9A\u7406\u7684\u8BC1\u660E, \u8BF7\u770B:"),m={href:"http://baike.baidu.com/view/79282.htm",target:"_blank",rel:"noopener noreferrer"},k=a("http://baike.baidu.com/view/79282.htm"),s=o("<p>\u6240\u4EE5:</p><blockquote><p>7 \u2261 7 (mod 12)</p><p>(-2) \u2261 10 (mod 12)</p><p>7 -2 \u2261 7 + 10 (mod 12)</p></blockquote><p>\u73B0\u5728\u6211\u4EEC\u4E3A\u4E00\u4E2A\u8D1F\u6570, \u627E\u5230\u4E86\u5B83\u7684\u6B63\u6570\u540C\u4F59\u6570. \u4F46\u662F\u5E76\u4E0D\u662F7-2 = 7+10, \u800C\u662F 7 -2 \u2261 7 + 10 (mod 12) , \u5373\u8BA1\u7B97\u7ED3\u679C\u7684\u4F59\u6570\u76F8\u7B49.</p><p>\u63A5\u4E0B\u6765\u56DE\u5230\u4E8C\u8FDB\u5236\u7684\u95EE\u9898\u4E0A, \u770B\u4E00\u4E0B: 2-1=1\u7684\u95EE\u9898.</p><blockquote><p>2-1=2+(-1) = [0000 0010]\u539F + [1000 0001]\u539F= [0000 0010]\u53CD + [1111 1110]\u53CD</p></blockquote><p>\u5148\u5230\u8FD9\u4E00\u6B65, -1\u7684\u53CD\u7801\u8868\u793A\u662F1111 1110. \u5982\u679C\u8FD9\u91CC\u5C06[1111 1110]\u8BA4\u4E3A\u662F\u539F\u7801, \u5219[1111 1110]\u539F = -126, \u8FD9\u91CC\u5C06\u7B26\u53F7\u4F4D\u9664\u53BB, \u5373\u8BA4\u4E3A\u662F126.</p><p>\u53D1\u73B0\u6709\u5982\u4E0B\u89C4\u5F8B:</p><blockquote><p>(-1) mod 127 = 126</p><p>126 mod 127 = 126</p></blockquote><p>\u5373:</p><blockquote><p>(-1) \u2261 126 (mod 127)</p><p>2-1 \u2261 2+126 (mod 127)</p></blockquote><p>2-1 \u4E0E 2+126\u7684\u4F59\u6570\u7ED3\u679C\u662F\u76F8\u540C\u7684! \u800C\u8FD9\u4E2A\u4F59\u6570, \u6B63\u5F0F\u6211\u4EEC\u7684\u671F\u671B\u7684\u8BA1\u7B97\u7ED3\u679C: 2-1=1</p><p>\u6240\u4EE5\u8BF4\u4E00\u4E2A\u6570\u7684\u53CD\u7801, \u5B9E\u9645\u4E0A\u662F\u8FD9\u4E2A\u6570\u5BF9\u4E8E\u4E00\u4E2A\u819C\u7684\u540C\u4F59\u6570. \u800C\u8FD9\u4E2A\u819C\u5E76\u4E0D\u662F\u6211\u4EEC\u7684\u4E8C\u8FDB\u5236, \u800C\u662F\u6240\u80FD\u8868\u793A\u7684\u6700\u5927\u503C! \u8FD9\u5C31\u548C\u949F\u8868\u4E00\u6837, \u8F6C\u4E86\u4E00\u5708\u540E\u603B\u80FD\u627E\u5230\u5728\u53EF\u8868\u793A\u8303\u56F4\u5185\u7684\u4E00\u4E2A\u6B63\u786E\u7684\u6570\u503C!</p><p>\u800C2+126\u5F88\u663E\u7136\u76F8\u5F53\u4E8E\u949F\u8868\u8F6C\u8FC7\u4E86\u4E00\u8F6E, \u800C\u56E0\u4E3A\u7B26\u53F7\u4F4D\u662F\u53C2\u4E0E\u8BA1\u7B97\u7684, \u6B63\u597D\u548C\u6EA2\u51FA\u7684\u6700\u9AD8\u4F4D\u5F62\u6210\u6B63\u786E\u7684\u8FD0\u7B97\u7ED3\u679C.</p><p>\u65E2\u7136\u53CD\u7801\u53EF\u4EE5\u5C06\u51CF\u6CD5\u53D8\u6210\u52A0\u6CD5, \u90A3\u4E48\u73B0\u5728\u8BA1\u7B97\u673A\u4F7F\u7528\u7684\u8865\u7801\u5462? \u4E3A\u4EC0\u4E48\u5728\u53CD\u7801\u7684\u57FA\u7840\u4E0A\u52A01, \u8FD8\u80FD\u5F97\u5230\u6B63\u786E\u7684\u7ED3\u679C?</p><blockquote><p>2-1=2+(-1) = [0000 0010]\u539F + [1000 0001]\u539F = [0000 0010]\u8865 + [1111 1111]\u8865</p></blockquote><p>\u5982\u679C\u628A[1111 1111]\u5F53\u6210\u539F\u7801, \u53BB\u9664\u7B26\u53F7\u4F4D, \u5219:</p><blockquote><p>[0111 1111]\u539F = 127</p></blockquote><p>\u5176\u5B9E, \u5728\u53CD\u7801\u7684\u57FA\u7840\u4E0A+1, \u53EA\u662F\u76F8\u5F53\u4E8E\u589E\u52A0\u4E86\u819C\u7684\u503C:</p><blockquote><p>(-1) mod 128 = 127</p><p>127 mod 128 = 127</p><p>2-1 \u2261 2+127 (mod 128)</p></blockquote><p>\u6B64\u65F6, \u8868\u76D8\u76F8\u5F53\u4E8E\u6BCF128\u4E2A\u523B\u5EA6\u8F6C\u4E00\u8F6E. \u6240\u4EE5\u7528\u8865\u7801\u8868\u793A\u7684\u8FD0\u7B97\u7ED3\u679C\u6700\u5C0F\u503C\u548C\u6700\u5927\u503C\u5E94\u8BE5\u662F[-128, 128].</p><p>\u4F46\u662F\u7531\u4E8E0\u7684\u7279\u6B8A\u60C5\u51B5, \u6CA1\u6709\u529E\u6CD5\u8868\u793A128, \u6240\u4EE5\u8865\u7801\u7684\u53D6\u503C\u8303\u56F4\u662F[-128, 127]</p>",21);function _(x,f){const e=r("ExternalLinkIcon");return d(),l("div",null,[h,p("p",null,[p("a",b,[n,t(e)])]),i,p("p",null,[q,p("a",m,[k,t(e)])]),s])}var v=c(u,[["render",_],["__file","\u539F\u7801\u53CD\u7801\u548C\u8865\u7801.html.vue"]]);export{v as default};
